export default {
  data: function data() {
    return {
      uname: "",
      upwd: ""
    };
  },
  methods: {
    login: function login() {
      var _this = this;

      var reg = /^[a-z0-9]{3,12}$/i;
      var u = this.uname;
      var p = this.upwd;

      if (reg.test(u) == false) {
        this.$messagebox("用户名格式不正确");
        return;
      }

      if (reg.test(p) == false) {
        this.$messagebox("密码格式不正确");
        return;
      }

      var url = "login";
      var obj = {
        uname: u,
        upwd: p
      };
      this.axios.get(url, {
        params: obj
      }).then(function (res) {
        //console.log(res);
        if (res.data.code == 1) {
          _this.$messagebox("登陆成功");
        } else {
          _this.$messagebox("登录失败");
        }
      });
    }
  }
};
