export default {
  data: function data() {
    return {
      uname: "",
      upwd: ""
    };
  },
  methods: {
    reg: function reg() {
      var re = /^[a-z0-9]{3,12}$/i;
      var u = this.uname;
      var p = this.upwd;
      var url = "reg";
      var obj = {
        uname: u,
        upwd: p
      };

      if (re.test(u) == false) {
        this.$messagebox("用户名格式不正确");
        return;
      }

      if (re.test(p) == false) {
        this.$messagebox("密码格式不正确");
        return;
      }

      this.axios.get(url, {
        params: obj
      }).then(function (res) {
        if (res.data.code == 0) {
          alert("用户名已注册");
        } else if (res.data.code == 1) {
          alert("注册成功");
        } else {
          alert("注册失败");
        }
      }); // if(re.test(p)==false){
      //   this.$messagebox("密码格式不正确")
      // }
    }
  }
};
